import React, { Dispatch, SetStateAction, useEffect } from "react";
import "./style.css";

// interface: 페이지네이션 컴포넌트 Properties //
interface Props {
  currentPage: number;
  currentSection: number;
  setCurrentPage: Dispatch<SetStateAction<number>>;
  setCurrentSection: Dispatch<SetStateAction<number>>;

  viewPageList: number[];
  totalComments: number; // 총 댓글 수
}

// component: 페이지네이션 컴포넌트 //
export default function Pagination(props: Props) {
  // state: Properties //
  const { currentPage, currentSection, viewPageList, totalComments } = props;
  const { setCurrentPage, setCurrentSection } = props;

  // 페이지당 댓글 수
  const commentsPerPage = 10;

  // 총 섹션 수 계산
  const totalSection = Math.ceil(totalComments / commentsPerPage);

  // 컴포넌트가 처음 렌더링될 때 초기 페이지 설정
  useEffect(() => {
    if (totalSection > 0 && currentSection > totalSection) {
      setCurrentSection(totalSection);
      setCurrentPage((totalSection - 1) * commentsPerPage + 1);
    } else if (currentSection === 0) {
      setCurrentSection(1);
      setCurrentPage(1);
    }
  }, [
    totalComments,
    currentSection,
    totalSection,
    setCurrentPage,
    setCurrentSection,
  ]);

  // event handler: 페이지 클릭 이벤트 처리 //
  const onPageClickHandler = (page: number) => {
    setCurrentPage(page);
  };
  // event handler: 이전 클릭 이벤트 처리 //
  const onPreviousClickHandler = () => {
    if (currentSection === 1) return;
    setCurrentPage((currentSection - 1) * commentsPerPage);
    setCurrentSection(currentSection - 1);
  };
  // event handler: 다음 클릭 이벤트 처리 //
  const onNextClickHandler = () => {
    if (currentSection === totalSection) return;
    setCurrentPage(currentSection * commentsPerPage + 1);
    setCurrentSection(currentSection + 1);
  };

  // render: 페이지네이션 컴포넌트 렌더링//
  return (
    <div id="pagination-wrapper">
      <div className="pagination-change-link-box">
        <div className="icon-box-small">
          <div className="icon expand-left-light-icon"></div>
        </div>
        <div
          className="pagination-change-link-text"
          onClick={onPreviousClickHandler}
        >
          {"이전"}
        </div>
      </div>
      <div className="pagination-divider">{"|"}</div>

      {viewPageList.map((page) =>
        page === currentPage ? (
          <div className="pagination-text-active" key={page}>
            {page}
          </div>
        ) : (
          <div
            className="pagination-text"
            onClick={() => onPageClickHandler(page)}
            key={page}
          >
            {page}
          </div>
        )
      )}

      <div className="pagination-divider">{"|"}</div>
      <div className="pagination-change-link-box">
        <div
          className="pagination-change-link-text"
          onClick={onNextClickHandler}
        >
          {"다음"}
        </div>
        <div className="icon-box-small">
          <div className="icon expand-right-light-icon"></div>
        </div>
      </div>
    </div>
  );
}
