import React, { useEffect, useRef, useState } from "react";
import "./style.css";
import { User } from "types/interface";
import defaultProfileImage from "assets/image/default-profile-image.png";
import { useParams } from "react-router-dom";

// component: 유저 화면 컴포넌트 //
export default function UserPage() {
  // state: 마이페이지 여부 상태 //
  const { userEmail } = useParams();

  // component: 유저 화면 상단 컴포넌트 //
  const UserTop = () => {
    // state: 이미지 파일 인풋 참조 상태 //
    const imageInputRef = useRef<HTMLInputElement | null>(null);
    // state: 마이페이지 여부 상태 //
    const [isMyPage, setMyPage] = useState<boolean>(true);
    // state: 닉네임 변경 여부 상태 //
    const [isChangeNickname, setChangeNickname] = useState<boolean>(false);
    // state: user 정보 상태 //
    const [user, setUser] = useState<User | null>(null);

    // Effect: email path variable 변경 시 실행 할 함수 //
    useEffect(() => {
      if (!userEmail) return;
      setUser({
        email: "email@email.com",
        nickname: "에디",
        profileImage:
          "https://cdn.pixabay.com/photo/2024/02/26/19/39/monochrome-image-8598798_640.jpg",
      });
    }, [userEmail]);

    // render: 유저 화면 상단 컴포넌트 렌더링 //
    if (!user) return <></>;
    return (
      <div id="user-top-wrapper">
        <div className="user-top-container">
          {isMyPage ? (
            <div className="user-top-my-profile-image-box">
              {user.profileImage !== null ? (
                <div
                  className="user-top-profile-image"
                  style={{
                    backgroundImage: `url(${user.profileImage})`,
                  }}
                ></div>
              ) : (
                <div className="user-top-my-profile-image-nothing-box">
                  <div className="icon-box-large">
                    <div className="icon image-box-white-icon"></div>
                  </div>
                </div>
              )}
              <input
                ref={imageInputRef}
                type="file"
                accept="image/*"
                style={{ display: "none" }}
              />
            </div>
          ) : (
            <div
              className="user-top-profile-image-box"
              style={{
                backgroundImage: `url(${user.profileImage})`,
              }}
            ></div>
          )}
          <div className="user-top-info-box">
            <div className="user-top-info-nickname-box">
              {isMyPage ? (
                <>
                  <div className="user-top-info-nickname">{user.nickname}</div>
                  <div className="icon-button">
                    <div className="icon edit-icon"></div>
                  </div>
                </>
              ) : (
                <div className="user-top-info-nickname">{user.nickname}</div>
              )}
            </div>
            <div className="user-top-info-email">{"email3@email3.com"}</div>
          </div>
        </div>
      </div>
    );
  };

  // component: 유저 화면 하단 컴포넌트 //

  const UserBottom = () => {
    // render: 유저 화면 하단 컴포넌트 렌더링 //
    return <div></div>;
  };

  // render: 유저 화면 컴포넌트 렌더링 //
  return (
    <>
      <UserTop />
      <UserBottom />
    </>
  );
}
