import React from "react";
import "./style.css";
import defaultProfileImage from "assets/image/default-profile-image.png";
import { BoardListItem } from "types/interface";

interface Props {
  top3ListItem: BoardListItem;
}

// component: Top 3 List Item 컴포넌트 //
export default function Top3Item({ top3ListItem }: Props) {
  // properties //
  const { boardNumber, title, content, boardTitleImage } = top3ListItem;
  const { favoriteCount, commentCount, viewCount } = top3ListItem;
  const { writeDatetime, writerNickname, writerProfileImage } = top3ListItem;

  // render: Top 3 List Item 컴포넌트 렌더링 //
  return (
    <div
      className="top-3-list-item"
      style={{ backgroundImage: `url(${boardTitleImage})` }}
    >
      <div className="top-3-list-item-main-box">
        <div className="top-3-list-item-top">
          <div className="top-3-list-item-profile-box">
            <div
              className="top-3-list-item-profile-image"
              style={{
                backgroundImage: `url(${
                  writerProfileImage ? writerProfileImage : defaultProfileImage
                })`,
              }}
            ></div>
          </div>
          <div className="top-3-list-item-write-box">
            <div className="top-3-list-item-nickname">{"writerNickname"}</div>
            <div className="top-3-list-item-write-date">{"2024. 09. 21."}</div>
          </div>
        </div>
        <div className="top-3-list-item-middle">
          <div className="top-3-list-item-title">
            {"오늘 운동 뭐하지 추천좀 ...오늘 운동 뭐하지 추천좀 ... "}
          </div>
          <div className="top-3-list-item-content">
            {
              "제목이 곧 내용 뭐하지 ...제목이 곧 내용 뭐하지 ...제목이 곧 내용 뭐하지 ... "
            }
          </div>
        </div>
        <div className="top-3-list-item-bottom">
          <div className="top-3-list-item-counts">
            {`댓글 0  ·  좋아요 0  ·  조회수 0`}
          </div>
        </div>
      </div>
    </div>
  );
}
