import InputBox from "components/InputBox";
import "./style.css";
import React, { useState, KeyboardEvent, useRef } from "react";
import { SignInRequestDto } from "apis/request/auth";

// component : 인증 화면 컴포넌트 //
export default function Authentication() {
  // state: 화면 상태 //
  const [view, setView] = useState<"sign-in" | "sign-up">("sign-in");

  // component : sign in card 컴포넌트 //
  const SignInCard = () => {
    // state: 이메일 요소 참조 상태 //
    const emailRef = useRef<HTMLInputElement | null>(null);
    // state: 패스워드 요소 참조 상태 //
    const passwordRef = useRef<HTMLInputElement | null>(null);

    // state: 이메일 상태 //
    const [email, setEmail] = useState<string>("");
    // state: 패스워드 상태 //
    const [password, setPassword] = useState<string>("");
    // state: 패스워드 타입 상태 //
    const [passwordType, setPasswordType] = useState<"text" | "password">(
      "password"
    );
    // state: 패스워드 버튼 아이콘 상태 //
    const [passwordButtonIcon, setPasswordButtonIcon] = useState<
      "eye-light-off-icon" | "eye-light-on-icon"
    >("eye-light-off-icon");
    // state: 에러 상태 //
    const [error, setError] = useState<boolean>(false);

    // event handler: 로그인 버튼 클릭 이벤트 처리  함수 //
    const onSignInButtonClickHandler = () => {
      const requestBody: SignInRequestDto = {
        email,
        password,
      };
    };
    // event handler: 회원가입 링크 클릭 이벤트 처리  함수 //
    const onSignLinkClickHandler = () => {
      setView("sign-up");
    };

    // event handler: 패스워드 버튼 클릭 이벤트 처리  함수 //
    const onPasswordButtonClickHandler = () => {
      if (passwordType === "text") {
        setPasswordType("password");
        setPasswordButtonIcon("eye-light-off-icon");
      } else {
        setPasswordType("text");
        setPasswordButtonIcon("eye-light-on-icon");
      }
    };

    // event handler: 이메일 인풋 키 다운 이벤트 처리  함수 //
    const onEmailKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
      if (event.key !== "Enter") return;
      if (!passwordRef.current) return;
      passwordRef.current.focus();
    };
    // event handler: 패스워드 인풋 키 다운 이벤트 처리  함수 //
    const onPasswordKeyDownHandler = (
      event: KeyboardEvent<HTMLInputElement>
    ) => {
      if (event.key !== "Enter") return;
      onSignInButtonClickHandler();
    };
    // render : sign in card 컴포넌트 렌더링 //
    return (
      <div className="auth-card">
        <div className="auth-card-box">
          <div className="auth-card-top">
            <div className="auth-card-title-box">
              <div className="auth-card-title">{"로그인"}</div>
            </div>
            <InputBox
              ref={emailRef}
              label="이메일 주소"
              type="text"
              placeholder="'이메일 주소를 입력해주세요."
              error={error}
              value={email}
              setValue={setEmail}
              onKeyDown={onEmailKeyDownHandler}
            />
            <InputBox
              ref={passwordRef}
              label="비밀번호"
              type={passwordType}
              placeholder="비밀번호를 입력해주세요."
              error={error}
              value={password}
              setValue={setPassword}
              icon={passwordButtonIcon}
              onButtonClick={onPasswordButtonClickHandler}
              onKeyDown={onPasswordKeyDownHandler}
            />
          </div>
          <div className="auth-card-bottom">
            {error && (
              <div className="auth-sign-in-error-box">
                <div className="auth-sign-in-error-message">
                  {
                    "이메일 주소 또는 비밀번호를 잘못 입력했습니다.\n입력하신 내용을 다시 확인해주세요."
                  }
                </div>
              </div>
            )}
            <div
              className="black-large-full-button"
              onClick={onSignInButtonClickHandler}
            >
              {"로그인"}
            </div>
            <div className="auth-description-box">
              <div className="auth-description">
                {"신규 사용자이신가요? "}
                <span
                  className="auth-description-link"
                  onClick={onSignLinkClickHandler}
                >
                  {"회원가입"}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // component : sign up card 컴포넌트 //
  const SignUpCard = () => {
    // render : sign up card 컴포넌트 렌더링 //
  };

  // render : 인증화면 컴포넌트 렌더링 //
  return (
    <div id="auth-wrapper">
      <div className="auth-container">
        <div className="auth-jumbotron-box">
          <div className="auth-jumbotron-contents">
            <div className="auth-logo-icon"></div>
            <div className="auth-jumbotron-text-box">
              <div className="auth-jumbotron-text">{"운동 커뮤니티"}</div>
              <div className="auth-jumbotron-text">{"Play Fit"}</div>
            </div>
          </div>
        </div>
        {view === "sign-in" && <SignInCard />}
        {/* {view === "sign-up" && <SignUpCard />} */}
      </div>
    </div>
  );
}
